import React, { Component } from 'react';
import { ValueConsumer } from '../../../Context';
import OriginalAmount from '../../atoms/main/OriginalAmount';
import DiscountAmount from '../../atoms/main/DiscountAmount';
import DeliveryAmount from '../../atoms/main/DeliveryAmount';
import GrandTotal from '../../atoms/main/GrandTotal';
import './billingAmount.scss';

class BillingAmount extends Component {
    render() {
        return (
            <ValueConsumer>{value =>
                <div className='billingAmount'>
                    <OriginalAmount originalAmount={value.cart.mrp} />
                    <DiscountAmount productDiscount={value.cart.mrp-value.cart.total} />
                    <DeliveryAmount />
                    <GrandTotal grandTotal={value.cart.total} />
                </div>
            }</ValueConsumer>
        )
    }
}

export default BillingAmount
