import React, { Component } from 'react';
import data from './items.json';
import HeaderTemplate from './homePage/templates/HeaderTemplate';
import MainContent from './homePage/templates/MainContent';
import ServiceTempplate from './homePage/templates/ServiceTemplate';
import Foot from './homePage/UI/organisms/Foot';
import { ValueProvider } from './homePage/Context';

export class HomePage extends Component {
    constructor(props) {
        super(props)
        this.state = {
            items: data
        }
    }
    addItem = (i) => {
        this.setState(prevState => {
            this.state.items[i].count = prevState.items[i].count + 1;
            console.log(prevState.items[i].count);
            console.log(this.state.items[i].count);
        })
    }

    render() {
        return (
            <>
                <HeaderTemplate />
                <ValueProvider value={
                    {
                        items: this.state.items,
                        addItem: this.addItem
                    }
                }>
                    <MainContent />
                </ValueProvider>
                <ServiceTempplate />
                <Foot />
            </>
        )
    }
}

export default HomePage
