import React, { Component } from 'react'

class BillItemDetail extends Component {
    render() {
        const {name, weight} = this.props;
        const itemWeight = weight>=1 ? weight+"kg" : (weight*1000)+"g";
        return (
            <>
                <div class="billItemName">{name}</div>
                <div class="billItemWeight">{itemWeight}</div>
            </>
        )
    }
}

export default BillItemDetail
