import React, { Component } from 'react';
import { ValueConsumer } from '../../../Context';

class Sort extends Component {
    constructor(props) {
        super(props)
        this.sortRef = React.createRef();
        this.optionValues = [
            {
                name: "All",
                value: "all"
            }, {
                name: "Veg",
                value: "veg"
            }, {
                name: "Non-veg",
                value: "non-veg"
            }
        ]
    }

    render() {
        return (
            <div className='sort'>
                <p>Sort By</p>
                <ValueConsumer>
                    {value => {
                        return <select name='sortBy' id='sortBy' ref={this.sortRef} onChange={() => value.sort(this.sortRef.current.value)}>
                        {
                            this.optionValues.map((option, index) => <option key={index} value={option.value}>{option.name}</option>)
                        }
                        </select>
                    }}       
                </ValueConsumer>
            </div>
        )
    }
}

export default Sort
